import { GET_TODOS, getTodos } from "./Actions";
import { getTodosData } from "./index";


export async function handleGetToDos (store) {
    const { dispatch } = store
    try {
        const data = await getTodosData()
        dispatch(getTodos(data))
    } catch (error) {
        console.log(error)
    }
}

export default store => next => action => {
    const { type } = action
    next(action)
    // Trigger actions after reducers have set state i.e. after next(action)
    switch (type) {
        case GET_TODOS:
            handleGetToDos(store)
            break
        default:
            break
    }
}
