import React from 'react'
import Button from "react-bootstrap/Button";

function TodoItems (props) {
    //const [buttonLabel, setButtonLabel]

    async function deleteItem (e) {
        console.log(e.currentTarget.id)
        await props.deleteTodos(e.currentTarget.id)
    }

    async function toggleUpdateItem (e) {
        console.log(e.currentTarget.id, 'toggle')
        console.log(props.entries)
        props.toggleUpdate(e.currentTarget.id)
    }

    function createTasks (item) {
        return <li key={item.id} className="list-group-item list-group-item-action">
            <div className="input-group" >
                <p className="label listLabel">{item.resource.description}</p>
                <Button onClick={deleteItem} id={item.id}>Delete
                </Button>
                <Button onClick={toggleUpdateItem} id={item.id}>Update
                </Button>
            </div>
        </li>
    }
    const todoEntries = props.entries
    const listItems = todoEntries && todoEntries.length > 0 ? todoEntries.map(createTasks) : null

    return (
         <ul className="list-group listFrame">{listItems}</ul>
    )
}
export default TodoItems
