import axios from 'axios';

export default {
    getTodos,
    createTodos
}

async function getTodos () {
    let todosList = []

    await axios.get(`https://h4sk8blscl.execute-api.us-east-1.amazonaws.com/dev/todos`)
        .then(res => {
            todosList = res.data;
        })
    return todosList.data
}

async function createTodos (description) {
    let todosList = []

    const body = {
        "todo": [description]
    };
    await axios.post('https://h4sk8blscl.execute-api.us-east-1.amazonaws.com/dev/todos', body)
        .then(res => {
            todosList = res.data;
        })
    return todosList.data
}

async function deleteTodos (description) {
    let todosList = []

    const body = {
        "todo": [description]
    };
    await axios.post('https://h4sk8blscl.execute-api.us-east-1.amazonaws.com/dev/todos', body)
        .then(res => {
            todosList = res.data;
        })
    return todosList.data
}
