import React from 'react';
import axios from 'axios';

export default {
    get_todos,
}

const get_todos = () => {
    let todosList = []

    axios.get(`https://github.com/gorillalogic/l-l-react-aws/tree/master/todo-aws`)
        .then(res => {
            todosList = res.data;
            return todosList
        })

}
