import React, { Component } from 'react';
import './App.css';
import ToDoList from './components/ToDoList';
import ToDoItems from './components/TodoItems';
import awsService from './services/services'

const App = () => {
  const initialState = {text: ''}
  const [items, setItems] = useState([])
  const [currentItem, setCurrentItem] = useState(initialState)
  const [updating, setUpdating] = useState(false)

  async function getTodos () {
    const todoList = await awsService.getTodos()
    setItems(todoList)
  }

  useEffect(() => {
    const fetchData = async () =>{
       await getTodos()
    }
    fetchData();
  }, [])

  const deleteTodos = async (id) => {
    await awsService.deleteTodos(id);
    await getTodos();
  }

  const handleInput = (e) => {
    const itemText = e.target.value;
    const newItem = { text: itemText}
    setCurrentItem(newItem)
  }

  async function addItem (e) {
    e.preventDefault()
    const newItem = currentItem
    if (newItem.text !== '') {
      console.log(newItem)

      await awsService.createTodos(currentItem.text);
      await getTodos()
      const emptyItem = { text: ''}
      setCurrentItem(emptyItem)
    }
  }

  const inputElement = React.createRef()

  return (
      <div className="">
        <header className="">
          <ToDoList
              addItem={addItem}
              inputElement={inputElement}
              handleInput={handleInput}
              currentItem={currentItem}
          />
          <ToDoItems
              entries={items}
              deleteTodos={deleteTodos}
          />
        </header>
      </div>
  );

}

export default App;
