import axios from 'axios';
import { getTodos } from './Actions'

export function getTodosData () {
    return (dispatch) => {
        return axios.get('https://h4sk8blscl.execute-api.us-east-1.amazonaws.com/dev/todos')
            .then(response => {
                console.log(response.data)
                dispatch(getTodos(response.data))
            })
            .catch(error => {
                throw(error);
            });
    };

}

export function createTodosData (todo) {
    return (dispatch) => {
        return axios.post('https://h4sk8blscl.execute-api.us-east-1.amazonaws.com/dev/todos', todo)
            .then(response => {
                dispatch(getTodos(response.data))
            })
            .catch(error => {
                throw(error);
            });
    };

}
