import React, {useEffect, useState} from 'react';
import './App.css';
import ToDoList from './components/ToDoList';
import ToDoItems from './components/TodoItems';
import awsService from './services/services'

function App () {
  const [items, setItems] = useState([])
  const [currentItem, setCurrentItem] = useState({text:''})

  async function getTodos () {
    const todoList = await awsService.getTodos()
    setItems({items: todoList})
  }

  useEffect(() => {
    getTodos()
  })

  async function deleteTodos(id) {
    await awsService.deleteTodos(id);
    getTodos();
  }

  function handleInput (e) {
    const itemText = e.target.value;
    const currentItem = { text: itemText}
    setCurrentItem({currentItem})
  }

  async function addItem (e) {
    e.preventDefault()
    const newItem = currentItem
    if (newItem.text !== '') {
      console.log(newItem)

      await awsService.createTodos(currentItem.text);
      await getTodos()

      this.setState({
        currentItem: { text: ''},
      })
    }
  }

  const inputElement = React.createRef()

  return (
      <div className="">
        <header className="">
          <ToDoList
              addItem={addItem}
              inputElement={inputElement}
              handleInput={handleInput}
              currentItem={currentItem}
          />
          <ToDoItems
              entries={items}
              deleteTodos={deleteTodos}
          />
        </header>
      </div>
  );

}

export default App;
