import React, { Component } from 'react'

class TodoItems extends Component {
    createTasks(item) {
        return <li key={item.id} className="list-group-item list-group-item-action">
            <div className="input-group">
                <p class="form-control">{item.resource.description}</p>
                <button>delete</button>
            </div>
        </li>
    }
    render() {
        const todoEntries = this.props.entries
        const listItems = todoEntries ? todoEntries.map(this.createTasks) : null
        return <ul className="list-group listFrame">{listItems}</ul>
    }
}
export default TodoItems
