/*
 * action types
 */

import axios from "axios";

export const ADD_TODO = 'ADD_TODO'
export const GET_TODOS = 'GET_TODOS'


/*
 * action creators
 */

export function addTodo(payload) {
    return {
        type: ADD_TODO,
        payload
    }
}

export function getTodos(payload) {
    return {
        type: GET_TODOS,
        payload
    }
}

export function getData () {
    return (dispatch) => {
        return axios.get('https://h4sk8blscl.execute-api.us-east-1.amazonaws.com/dev/todos')
            .then(response => {
                dispatch(getTodos(response.data))
            })
            .catch(error => {
                throw(error);
            });
    };

}
