import React from 'react'
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

function TodoList ({handleInput,currentItem = {text: ''}, inputElement, addItem, updating, updateTodo}) {
    return (
        <div >
            <Form onSubmit={updating ? updateTodo : addItem} className="inputForm" >
                <input
                    className="form-control"
                    placeholder="Task"
                    ref={inputElement}
                    value={currentItem.text}
                    onChange={handleInput}
                />
                <Button
                    type="submit"
                    variant="primary"
                    className="btn"> {updating ? 'Update': 'Add Todo'}
                </Button>
            </Form>
        </div>
    )
}
export default TodoList
