import {
    ADD_TODO,
} from './Actions'



function todos(state = [], action) {
    switch (action.type) {
        case ADD_TODO:
            return [
                ...state,
                {
                    text: action.payload.text,
                    key: action.payload.key
                }
            ]

        default:
            return state
    }
}


export default todos
