import React from 'react';
import axios from 'axios';

export function getTodos () {
        let todosList = []

        axios.get(`https://github.com/gorillalogic/l-l-react-aws/tree/master/todo-aws`)
            .then(res => {
                todosList = res.data;
                return todosList
            })
    }
